import React from "react";
import { useEffect, useState } from "react";
import BlogCard from "../Components/BlogCard";
import Contact from "../Components/Contact";

const blogUpdate = () => {
  const [blogs, setBlogs] = useState([]);
  useEffect(() => {
    fetch("../blogs")
      .then((response) => response.json())
      .then((data) => {
        setBlogs(data);
      });
  }, []);
  return (
    <div>
      <table className="mx-10">
        <tr className="">
          <th className="border p-2">Disease Name </th>
          <th className="border p-2">Update </th>
          <th className="border p-2">Delete </th>
        </tr>

        {blogs.map((blog) => (
          <tr>
            <td className="border p-2 w-full">
              <textarea
                className="border-2	rounded-sm textarea"
                type="text"
                id={blog._id}
                name=""
                defaultValue={blog.title}
              ></textarea>
            </td>
            <td className="border p-2">
              <div className="bg-green-600 text-center p-2 text-sm text-white rounded-sm hover:shadow-md">
                <input className="" type="submit" value="update"></input>
              </div>{" "}
            </td>
            <td className="border p-2">
              {" "}
              <div
                className="bg-red-600 p-2 text-sm text-white rounded-sm"
                onClick={(e) => deleteBlog(e, blog._id)}
                id={blog._id}
              >
                Delete
              </div>{" "}
            </td>
          </tr>
        ))}
      </table>
    </div>
  );
};

export default BlogUpdate;
