import React from "react";

const FAQuestions = () => {
  const question = [
    "What are the benefits of homeopathy? ",
    "Is it true that homeopathy medicine works very slowly?",
    "What if I face a problem during the course of treatment?",
  ];
  const answer = [
    "Homeopathy is free of steroids. It is infact derived from plants ad hence has no side effects. For this reason, it is best suitedto pregnant and lactating women , young children and even the  even the elderly. Homeopathy also cures many autoimmune diseases which have no cure in the mainstream medicine.",
    "With the right medication under an expert homeopath, you ca feel the relief in a short span of time. Moreover, the way HOMEOZ works, you will cure it permanently.",
    "You can always reach out to us and our team will be more than happy to help-answer any queries that you may have and help you understand as well as tackle anything hampering your recovery.",
  ];

  const faq = [
    {
      question: "What are the benefits of homeopathy? ",
      answer:
        "Homeopathy is free of steroids. It is infact derived from plants ad hence has no side effects. For this reason, it is best suitedto pregnant and lactating women , young children and even the  even the elderly. Homeopathy also cures many autoimmune diseases which have no cure in the mainstream medicine.",
    },
    {
      question: "Is it true that homeopathy medicine works very slowly?",
      answer:
        "With the right medication under an expert homeopath, you ca feel the relief in a short span of time. Moreover, the way HOMEOZ works, you will cure it permanently.",
    },
    {
      question: "What if I face a problem during the course of treatment?",
      answer:
        "You can always reach out to us and our team will be more than happy to help-answer any queries that you may have and help you understand as well as tackle anything hampering your recovery.",
    },
  ];

  return (
    <div>
      <div>
        <div className="text-center mt-10">
          <p className="text-primary font-medium">Frequently Asked Qusetions</p>
          <p className="font-bold text-xl"> Get Your General Answer</p>
        </div>
        <div className="flex justify-between md:p-10 items-center flex-col md:flex-row">
          <div className="md:w-1/3 w-8/12  py-10 md:py-0">
            <img
              src="/images/faq.png"
              className="rounded-md drop-shadow-md"
            ></img>
          </div>
          <div className=" bg-gray  md:w-2/3 px-10 h-full flex-col md:items-center  self-start">
            <div className=" ">
              <b className="question">{question[0]}</b>

              <p className="answer">
                <br></br>
                Homeopathy is free of steroids. It is infact derived from plants
                ad hence has no side effects. For this reason, it is best suited
                to pregnant and lactating women , young children and even the
                even the elderly. Homeopathy also cures many autoimmune diseases
                which have no cure in the mainstream medicine.
              </p>
            </div>

            {FAQuestions.map((fa, index) => (
              <div className=" ">
                <b className="question">{fa.question}</b>

                <p className="answer">
                  <br></br>
                  {fa.answer}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FAQuestion;
