import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const AppointmentSingle = () => {
  const { id } = useParams();
  const [appointment, setAppointment] = useState([]);
  useEffect(() => {
    const url = "/appointment/" + id;
    console.log(url);
    fetch(url)
      .then((response) => response.json())
      .then((appointment) => {
        setAppointment(appointment);
        console.log(appointment);
      });
  }, [appointment]);
  return (
    <div>
      {appointment ? (
        <div>
          <div>
            <div>Personal Details</div>
            <div>Name: {appointment?.personal?.name}</div>
            <div>Age: {appointment?.personal?.age}</div>
            <div>Gender: {appointment?.personal?.gender}</div>
            <div>Phone: {appointment?.personal?.phone}</div>
            <div>Symptoms: {appointment?.personal?.symptoms}</div>
            <div>
              {" "}
              Payment stauts{" "}
              {appointment?.personal?.payments ? "Success" : "Failed"}
            </div>
            <div>Consultation Details</div>

            {/* {appointment.consultation?.map((consult) => (
              <div>
                <div>Date: {consult.mode}</div>
                <div>Date: {consult.dateOfBooking}</div>
                <div>Date: {consult.status.length}</div>
              </div>
            ))} */}

            <div>Medicine History</div>
            {/* {appointment.medicine.maps((med) => (
              <div>
                <div>Order Id</div>
              </div>
            ))} */}
          </div>
        </div>
      ) : (
        <div></div>
      )}
    </div>
  );
};

export default AppointmentSingle;
