import React, { useState } from "react";
import { Link, useNavigate, useSearchParams } from "react-router-dom";

export const PaymentSuccess = () => {
  const searchQuery = useSearchParams()[0];
  const refNum = searchQuery.get("ref");
  const navigate = useNavigate();

  const sendHome = () => {
    navigate("/");
  };
  const [counter, setCounter] = useState(10000);
  setTimeout(sendHome, counter);
  // setInterval(()=>{
  //   setCounter(counter-1)
  // },counter)
  // if(counter===0){
  //   navigate('/')
  // }
  return (
    <>
      <div>Appointment Booking Successfull</div>
      <div>Reference Number-: {refNum}</div>
      <div>Automatic redirection in {10 + 1} seconds</div>
      <Link to="/">Click here to go Home</Link>
    </>
  );
};
