import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
// import Button from "./Button";

const PopUp = () => {
  if (localStorage.getItem("data")) localStorage.removeItem("data");
  const navigate = useNavigate();
  const [data, setData] = useState({
    name: "",
    phone: 0,
    email: "",
    gender: "",
    age: 0,
    symptom: "",
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    localStorage.setItem("data", JSON.stringify(data));
    navigate("/checkout");
    // <Checkout data={data}/>
    // console.log("data",data);
  };
  return (
    <div className="mt-28">
      <div className="flex justify-center font-bold text-3xl">
        Enter Patient Details{" "}
      </div>
      <div className="flex justify-center items-center py-10 w-screen ">
        <div className="lg:w-2/3 w-full py-10  flex  ">
          <div className="hidden md:flex w-1/2 items-center ">
            <img
              src="/images/registerbg1.jpg"
              className=""
              alt="Ni milaðŸ˜¢ðŸ˜¢"
            ></img>
          </div>
          <div className=" w-full md:w-1/2 p-6 ">
            <div className="mb-4 w-72">
              <label className=" ">Name</label>
              <input
                onChange={handleChange}
                value={data.name}
                name="name"
                className=" mt-2  p-4  drop-shadow-md  focus:border-primary w-full rounded-sm h-8"
              ></input>
            </div>
            <div className="mb-4 w-72">
              <label className="  ">Phone Number</label>
              <input
                onChange={handleChange}
                value={data.phone}
                name="phone"
                className=" mt-2 p-4 drop-shadow-md  focus:border-primary w-full rounded-sm h-8"
              ></input>
            </div>
            <div className="mb-4 w-72">
              <label className="  ">email</label>
              <input
                onChange={handleChange}
                value={data.email}
                name="email"
                className=" mt-2  p-4   drop-shadow-md  focus:border-primary w-full rounded-sm h-8"
              ></input>
            </div>
            <div className="mb-4 w-72">
              <label className="  ">GENDER</label>
              <input
                onChange={handleChange}
                value={data.gender}
                name="gender"
                className=" mt-2  p-4    drop-shadow-md  focus:border-primary w-full rounded-sm h-8"
              ></input>
            </div>
            <div className="mb-4 w-72">
              <label className="  ">AGE</label>
              <input
                onChange={handleChange}
                value={data.age}
                name="age"
                className=" mt-2 p-4   drop-shadow-md  focus:border-primary  w-full rounded-sm h-8"
              ></input>
            </div>
            <div className="mb-4 w-72">
              <label className="  ">Symptoms or breif of problem</label>
              <input
                onChange={handleChange}
                value={data.symptom}
                name="symptom"
                className=" mt-2 p-4    focus:border-primary w-full rounded-sm h-8"
              ></input>
            </div>
            {/* <Button text="Book " /> */}
            <button
              onClick={handleSubmit}
              style={{
                border: "2px solid black",
                padding: "0px 15px 0px 15px",
                backgroundColor: "beige",
              }}
            >
              Book
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PopUp;
