import React from "react";
import { useState, useEffect } from "react";

const Testinomials = () => {
  const [count, setCount] = useState(1);
  useEffect(() => {
    console.log(count);
  }, []); // <- add empty brackets here
  const handleClic = () => {};
  const data = [
    {
      title:
        "XYZ Consulting's online consulting service has been a game-changer for me. Their expert consultants provided tailored solutions,    exceptional responsiveness, and convenient online sessions. Thanks    to their guidance, my business has thrived. Highly recommended!",
      subtitle: "chief inspector",
    },
    {
      title: "jfsdjfkjsdfkjsdfjsdjfkdsjfis",
      subtitle: "jdkjsdkjfksdjfjsdkfjdskfjdskjfksdj",
    },
  ];
  return (
    <>
      <div></div>
      <div className="text-center my-12">
        <p className="text-primary font-medium">Testinomials</p>
        <p className="font-bold text-xl">What our patient Says</p>
      </div>

      <div className="lg:w-2/3 shadow-lg flex mx-auto justify-center items-center flex-col md:flex-row ">
        <div className="w-1/2  ">
          <img src="images/faq.png" className=""></img>
        </div>
        <div className="md:w-1/2 flex flex-col self-center items-center px-10  py-10 md:py-0  ">
          <div className="hidden md:block">
            <img src="/images/logo.png" className="w-20"></img>
          </div>
          <div className="maincontent text-md font-bold  ">{data[0].title}</div>
          <div className="subtitle pt-4">chief inspector</div>
        </div>
      </div>
      <div className="flex justify-center w-full py-2 gap-2">
        <a href="#item1" className="btn btn-xs" onClick={setCount(1)}>
          1
        </a>
        <a href="#item2" className="btn btn-xs" onClick={setCount(2)}>
          2
        </a>
        <a href="#item3" className="btn btn-xs" onClick={setCount(3)}>
          3
        </a>
        <a href="#item4" className="btn btn-xs" onClick={setCount(4)}>
          4
        </a>
      </div>
      <div className="flex justify-center pt-10">
        <button className="p-4 text-sm boundary bg-primary rounded-sm mt-4 text-white font-semibold">
          {" "}
          Next
        </button>
      </div>
    </>
  );
};

export default Testinomials;
