import React from "react";
import { useState, useEffect } from "react";

const BlogChange = () => {
  const [blogs, setBlogs] = useState([]);
  useEffect(() => {
    fetch("../blogs")
      .then((response) => response.json())
      .then((data) => {
        setBlogs(data);
        console.log(data);
      });
  }, []);

  const updateblogs = () => {};
  const deleteblogs = () => {};
  return (
    <div>
      <div className="px-10 sm:flex items-center justify-between">
        <div className="flex items-center">
          <img src="/images/logo.png" className="w-20 inline-block"></img>
          <h2 className="inline text-3xl font-semibold">Homeoz</h2>
        </div>
        <div className="flex items-center ">
          <div className="flex items-center bg-secondary py-2 px-4 mr-2">
            <h2 className="">
              {" "}
              <span className="font-bold text-xl"> &larr;</span> Back to main
              Website
            </h2>
          </div>

          <div className="bg-secondary  p-2 hover:bg-primary hover:text-white">
            Logout
          </div>
        </div>
      </div>
      <div className="w-80 h-10 flex  sm:mx-auto mx-10 my-10 sm:my-0">
        <div className="w-28 h-10 bg-primary mr-2 text-xs justify-center flex items-center">
          {" "}
          <div> Contents Update</div>
        </div>
        <div className="w-20 h-10 bg-secondary mr-2 text-xs justify-center flex items-center">
          Blogs
        </div>
        <div className="w-20 h-10 bg-secondary mr-2 text-xs justify-center flex items-center">
          Payments
        </div>
      </div>
      <div className="mx-10 mt-4 border-b-2 border-primary  font-bold text-lg ">
        <div>Blogs Update</div>

        <table className="mx-10">
          <tr className="">
            <th className="border p-2">blogs Name </th>
            <th className="border p-2">Update </th>
            <th className="border p-2">Delete </th>
          </tr>

          {blogs.map((blog) => (
            <tr>
              <td className="border p-2 w-full">
                <textarea
                  className="border-2	rounded-sm textarea"
                  type="text"
                  id={blogs._id}
                  name=""
                  defaultValue={blogs.title}
                ></textarea>
              </td>
              <td className="border p-2">
                <div className="bg-green-600 text-center p-2 text-sm text-white rounded-sm hover:shadow-md">
                  <input
                    className=""
                    type="submit"
                    value="update"
                    onClick={(e) => updateblogs(e, blogs)}
                  ></input>
                </div>{" "}
              </td>
              <td className="border p-2">
                {" "}
                <div
                  className="bg-red-600 p-2 text-sm text-white rounded-sm"
                  onClick={(e) => deleteblogs(e, blogs)}
                  id={blogs._id}
                >
                  Delete
                </div>{" "}
              </td>
            </tr>
          ))}
        </table>
        <hr></hr>
      </div>
    </div>
  );
};

export default BlogChange;
