import React from "react";
import "./Blog.css";
import { Link } from "react-router-dom";

const myComponentStyle = {};
const Blogs = () => {
  const url =
    "https://1.bp.blogspot.com/-kK7Fxm7U9o0/YN0bSIwSLvI/AAAAAAAACFk/aF4EI7XU_ashruTzTIpifBfNzb4thUivACLcBGAsYHQ/s1280/222.jpg";
  const featuredBlogs = [
    {
      title: "Addressing Allergies with Homeopathy: Finding Relief Naturally",
      img: url,
    },
    { title: "The Holistic Approach: Homeopathy and Wellness", img: url },
    {
      title:
        "Homeopathy for Stress and Anxiety: Restoring Balance in a Fast-Paced World",
      img: url,
    },
    {
      title: "Homeopathy for Children: Gentle Healing for Growing Bodies",
      img: url,
    },
  ];
  return (
    <div className=" ">
      <div className="text-center mt-10">
        <p className="text-primary font-medium">Blogs</p>
        <p className="font-bold text-xl"> Get ready for healhy blogs </p>
      </div>
      <div className="flex content-center justify-center overflow-x-auto w-full pl-[39rem] xl:pl-0 pb-10">
        {featuredBlogs.map((blog) => (
          <div className="h-80 w-64 m-8 flex rounded shadow-lg bg-white hover:shadow-2xl  bg-primary bg-[url({`url`})] bg-cover bg-center  transition-shadow duration-400 ease-in-out">
            <div className="sbg flex items-end h-80 w-64 rounded bg-[url('file:///Users/rajnish/Downloads/output-onlinepngtools.png')] bg-cover bg-center">
              <div className="text-white pb-4 px-4 font-bold">{blog.title}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="flex justify-center">
        <Link to="/blogs">
          <button className="p-4 text-sm boundary bg-primary rounded-sm mt-4 text-white font-bold">
            {" "}
            See More
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Blogs;
